import React, { useState } from "react";

const links = [
  {id: 1, name: 'Github', icon: 'fab fa-github fa-4x', link: 'https://github.com/abechoi'},
  {id: 1, name: 'Codepen', icon: 'fab fa-codepen fa-4x', link: 'https://github.com/abechoi'},
  {id: 1, name: 'LinkedIn', icon: 'fab fa-linkedin fa-4x', link: 'https://github.com/abechoi'},
];

const ToggleLink = () => {

  const [ dropdown, setDropdown ] = useState(true);

  return (
    <div className="toggle-link">
      <button className="toggle-btn" onClick={ setDropdown(!dropdown) }>

        { dropdown ? 

          <div className="toggle-dropdown">
            <div>SOCIAL</div>
            <div className="dropdown">
              { links.map(link => {
                <a key={link.id} href={link.link} target="__blank">
                  <i className={link.icon}></i>
                </a>
              })}
            </div>
          </div>
          :
          <div className="toggle-dropdown">
            NOTHING
          </div>

        }

      </button>
    </div>
  );
}

export default ToggleLink;
